import { runNeed, runOnce, base64 } from '@bext/util';
runOnce(() => {
    if (!window.mbrowser) {
        window.mbrowser = {
            isMonkeyScript: (c) => {
                let monk = false, cfm;
                [
                    'GM_info',
                    'GM_deleteValue',
                    'GM_getValue',
                    'GM_listValues',
                    'GM_setValue',
                    'GM_getResourceURL',
                    'GM_notification',
                    'GM_openInTab',
                    'GM_registerMenuCommand',
                    'GM_setClipboard',
                    'GM_xmlhttpRequest',
                    'GM_addStyle',
                    'GM_addElement',
                    'GM_addValueChangeListener',
                    'GM_removeValueChangeListener',
                    'GM_log',
                    'GM_getResourceText',
                    'GM_unregisterMenuCommand',
                    'GM_download',
                    'GM_getTab',
                    'GM_saveTab',
                    'GM_getTabs',
                    'GM_batch_download',
                ].forEach(m => {
                    if (c.indexOf(m) >= 0) monk = true;
                });
                if (monk) {
                    cfm = confirm('你要安装的脚本可能\n\
使用了油猴特有的接口。\n\
由于 Via 不支持这些接口，\n\
这个脚本可能不会正常执行。\n\n\
    确定继续安装吗？');
                    return (!cfm);
                } else return false;
            },
            openUrlOnNewTab: (u, n) => window.open(u),
            openUrl: (u, n, r) => location.assign(u),
            getBase64FromBlobData: u => via.download(u),
            showFloatMessageBox: (o, s) => {
                let fl = document.createElement("div"),
                    fb = document.createElement("div"),
                    fp = document.createElement("p");
                fl.style.cssText = `position:absolute;top:${window.scrollY}px;left:${window.scrollX}px;width:${window.innerWidth}px;height:${window.innerHeight}px;background:transparent;z-index:999999`;
                fb.style.cssText = `margin:0 auto;padding:0.5em;width:${window.innerWidth / 2}px;min-height:${window.innerWidth / 2}px;top:50%;left:50%;position:fixed;display:table;background:rgba(0,0,0,0.5);border-radius:2px;transform: translateX(-50%) translateY(-50%);z-index:999999`;
                fp.style.cssText = `font-size:30px;color:white;display:table-cell;vertical-align:middle;overflow-wrap:anywhere;z-index:999999`;
                fp.innerText = s;
                fl.ontouchstart = e => document.body.removeChild(e.currentTarget);
                fb.appendChild(fp);
                fl.appendChild(fb);
                document.body.appendChild(fl);
            },
            copyToClipboard: (s) => {
                let ha = document.createElement("textarea");
                ha.style.cssText = "position:absolute;top:-100%";
                ha.setAttribute("readonly", "");
                ha.value = s;
                document.body.appendChild(ha);
                ha.select();
                document.execCommand("copy");
                document.body.removeChild(ha);
                via.toast('拷贝至了剪贴板');
            },
            showToast: s => { via.toast(s) },
            addAdBlockRule: (r, a, b, t) => {
                let rule;
                if (t === 0 && r.include('##')) {
                    rule = r.split('##');
                    via.record(a ? a : rule[0], rule[1]);
                } else if (t === 0) {
                    via.record(a ? a : '*', r);
                } else if (t === 3) {
                    rule = r.split('->')[0].split('##');
                    via.record(a ? a : '*', rule[1]);
                } else {
                    via.toast('不支持此广告规则');
                }
            },
            getUserScriptListByJson: () => "[]",
            fetchScript: function (id) {
                fetch('https://srvcn.xbext.com/mobile/visits_resource?resource_id=' + id).then(r => r.json()).then(d => {
                    if ((d.extra.slice(0, 3) === 'ep.') && confirm('你要安装的脚本是\n\
X 浏览器的菜单触发脚本。\n\
由于 Via 不支持添加菜单按钮和\n\
长按触发脚本。\n\
因此在安装以后会直接加载执行。\n\
这可能不是你所期望的效果。\n\n\
    确定继续安装吗？')===false) return;
                    if (this.isMonkeyScript(d.content)) return;
                    via.addon(base64(JSON.stringify({
                        id: (parseInt(d.id) % 10000) + 990000,
                        name: d.title,
                        author: d.nickname,
                        url: (d.extra.slice(0, 3) === 'ep.') ? '*' : d.extra,
                        code: base64(d.content)
                    })));
                });
            },
            scriptInstalled: id => false,
            getToken: () => "ffffffffffffffffffffffffffffffff",
            getVersionCode: () => 400
        }
    }
    runNeed(
        () => location.host === "srvcn.xbext.com" &&
            document.getElementById('check-browser-note'),
        () => {
            document.querySelectorAll('.my-cell').forEach(c => {
                c.querySelector('.icon-xiazai').addEventListener('click', mbrowser.fetchScript.bind(mbrowser, c.id.slice(1)));
        });
    })
})
